function MediaProCheckInfo()
{
    global $modSettings, $boardurl;

    if (isset($modSettings['mediapro_copyrightkey']))
    {
        $m = 36;
        if (!empty($modSettings['mediapro_copyrightkey']))
        {
            if ($modSettings['mediapro_copyrightkey'] == sha1($m . '-' . $boardurl))
            {
                return false;
            }
            else
                return true;
        }
    }

    return true;
}

