operation.search:
// Action and board are both empty... BoardIndex!
		if (empty($board) && empty($topic))
		{
			require_once($sourcedir . '/BoardIndex.php');
			return 'BoardIndex';
		}
		// Topic is empty, and action is empty.... MessageIndex!

operation.add:
// EzPortal
global $ezpSettings;
if ($ezpSettings['ezp_portal_enable'] == 1 && empty($board) && empty($topic))
{


	    if (!empty($ezpSettings['ezp_disablemobiledevices']))
	    {

		    $user_agents = array(
				array('iPhone', 'iphone'),
				array('iPod', 'ipod'),
				array('PocketIE', 'iemobile'),
				array('Opera Mini', isset($_SERVER['HTTP_X_OPERAMINI_PHONE_UA']) ? $_SERVER['HTTP_X_OPERAMINI_PHONE_UA'] : "Opera Mini"),
				array('Opera Mobile', 'Opera Mobi'),
				array('Android', 'android'),
				array('Symbian', 'symbian'),
				array('BlackBerry', 'blackberry'),
				array('BlackBerry Storm', 'blackberry05'),
				array('Palm', 'palm'),
				array('Web OS', 'webos'),
			);

			foreach ($user_agents as $ua)
			{
				$string = (string) $ua[1];

				if (!empty($string))
				if ((strpos(strtolower($_SERVER['HTTP_USER_AGENT']), $string)))
				{
					require_once($sourcedir . '/BoardIndex.php');
					return 'BoardIndex';
				}

			}
	    }


			require_once($sourcedir . '/EzPortal.php');
			return 'EzPortalForumHomePage';
}


// Action and board are both empty... BoardIndex!
if (empty($board) && empty($topic))
{
	require_once($sourcedir . '/BoardIndex.php');
	return 'BoardIndex';
}
// Topic is empty, and action is empty.... MessageIndex!

// EzPortal Admin
if (allowedTo(array('ezportal_page', 'ezportal_blocks', 'ezportal_manage')))
{
	$context['admin_areas']['ezportal'] = array(
		'title' => $txt['ezportal_admin'],
		'areas' => array()
	);

	if (allowedTo('ezportal_manage'))
		$context['admin_areas']['ezportal']['areas']['ezportal_admin'] =  '<a href="' . $scripturl . '?action=ezportal;sa=settings;sesc=' . $sc . '">' . $txt['ezportal_settings'] . '</a>';

	if (allowedTo('ezportal_blocks') || allowedTo('ezportal_manage'))
		$context['admin_areas']['ezportal']['areas']['ezportal_blocks'] = '<a href="' . $scripturl . '?action=ezportal;sa=blocks;sesc=' . $sc . '">' . $txt['ezportal_block_manager'] . '</a>';

	if (allowedTo('ezportal_page') || allowedTo('ezportal_manage'))
		$context['admin_areas']['ezportal']['areas']['ezportal_pages'] = '<a href="' . $scripturl . '?action=ezportal;sa=pagemanager;sesc=' . $sc . '">' . $txt['ezportal_page_manager'] . '</a>';
}

// End EzPortal Admin


// EzPortal
if (isset($context['current_action2']))
	$current_action = $context['current_action2'];


// EzPortal Forum Button
global $ezpSettings;

if ($ezpSettings['ezp_portal_enable'] == 1)
	echo ($current_action == 'forum' || $context['browser']['is_ie4']) ? '<td class="maintab_active_' . $first . '">&nbsp;</td>' : '' , '
		<td valign="top" class="maintab_' , $current_action == 'forum' ? 'active_back' : 'back' , '">
			<a href="', $scripturl, '?action=forum">' , $txt['ezp_forum_tab'] , '</a>
		</td>' , $current_action == 'forum' ? '<td class="maintab_active_' . $last . '">&nbsp;</td>' : '';

operation.search:
// Set the top level linktree up.
	array_unshift($context['linktree'], array(
		'url' => &$scripturl,
		'name' => &$context['forum_name']
	));

operation.add:
// Compatibility.
	if (!isset($settings['theme_version']))
		$modSettings['memberCount'] = $modSettings['totalMembers'];

	 global $ezpSettings;

// Set the top level linktree up.
   array_unshift($context['linktree'], array(
      'url' => &$scripturl,
      'name' => &$context['forum_name']
   ), array('url' => $scripturl . '?action=forum', 'name' => $txt['ezp_forum_tab']));
