function template_tcs_settings(){	global $context, $scripturl, $txt, $modSettings;	echo'	<div id="admincenter">		<div class="cat_bar">			<h3 class="catbg">				'.$txt['tcs_admin_btn'].'			</h3>		</div>		<form method="post" action="', $scripturl, '?action=admin;area=comments" accept-charset="', $context['character_set'], '">			<div class="windowbg2">				<span class="topslice"><span></span></span>				<div class="content">					<dl class="settings">						<dt>							<label for="tcs_admin_enable">', $txt['tcs_admin_enable'], '</label>						</dt>						<dd>							<input type="checkbox" value="1" name="tcs_enabled" ',!empty($modSettings['tcs_enabled']) ? 'checked="checked"' : '',' />						</dd>					</dl>					<hr class="hrcolor clear">					<dl class="settings">						<dt>							<span><label for="tcs_admin_initial_number">', $txt['tcs_admin_initial_number'], '</label></span>						</dt>						<dd>							<input type="text" size="50" value="',!empty($modSettings['tcs_initial_number']) ? $modSettings['tcs_initial_number'] : '','" name="tcs_initial_number" />						</dd>						<dt>							<span><label for="tcs_admin_see_more_number">', $txt['tcs_admin_see_more_number'], '</label></span>						</dt>						<dd>							<input type="text" size="50" value="',!empty($modSettings['tcs_see_more_number']) ? $modSettings['tcs_see_more_number'] : '','" name="tcs_see_more_number" />						</dd>						<dt>							<span><label for="tcs_admin_characters_limit">', $txt['tcs_admin_characters_limit'], '</label></span><br />							<span class="smalltext">', $txt['tcs_admin_characters_limit_desc'], '</span>						</dt>						<dd>							<input type="text" size="50" value="',!empty($modSettings['tcs_characters_limit']) ? $modSettings['tcs_characters_limit'] : 0,'" name="tcs_characters_limit" />						</dd>						<dt>							<label for="tcs_admin_bbc">', $txt['tcs_admin_bbc'], '</label>						</dt>						<dd>							<input type="checkbox" value="1" name="tcs_bbc" ',!empty($modSettings['tcs_bbc']) ? 'checked="checked"' : '',' />						</dd>						<dt>							<label for="tcs_admin_smiles">', $txt['tcs_admin_smiles'], '</label>						</dt>						<dd>							<input type="checkbox" value="1" name="tcs_smiles" ',!empty($modSettings['tcs_smiles']) ? 'checked="checked"' : '',' />						</dd>					</dl>					<hr class="hrcolor clear" />					<div class="righttext">						<input type="submit" name="save" value="',$txt['tcs_admin_btn_save'],'" />					</div>				</div>				<span class="botslice"><span></span></span>			</div>		</form>	</div>';
}function template_tcs_permissions_board(){	global $context, $scripturl, $txt, $settings;	echo'	<div id="admincenter">		<div class="cat_bar">			<h3 class="catbg">				'.$txt['tcs_admin_perm_board'].'			</h3>		</div>		<form method="post" action="', $scripturl, '?action=admin;area=comments;sa=permissionsboard" accept-charset="', $context['character_set'], '">			<div class="windowbg2">				<span class="topslice"><span></span></span>				<div class="content">					<dl class="settings">						<dt>							<span><label for="tcs_admin_board_enabled">', $txt['tcs_admin_board_enabled'], ' <br /><span class="smalltext">', $txt['tcs_admin_board_enabled_desc'], '</span></label></span>						</dt>						<dd>							<select name="boards[]" size="10" multiple="multiple" style="width: 88%;">';							$id_boards = !empty($context['tcs_board_enabled']) ? $context['tcs_board_enabled'] : 0 ;							foreach ($context['jump_to'] as $category)							{								echo '								<option disabled="disabled">----------------------------------------------------</option>								<option disabled="disabled">', $category['name'], '</option>								<option disabled="disabled">----------------------------------------------------</option>';								foreach ($category['boards'] as $board)									echo '									<option value="' ,$board['id'], '" ' ,isset($id_boards) ? (in_array($board['id'], $id_boards) ? 'selected="selected"' : '') : '', '> ' . str_repeat('&nbsp;&nbsp;&nbsp; ', $board['child_level']) . '|--- ' . $board['name'] . '</option>';							}							echo '							</select>						</dd>					</dl>					<hr class="hrcolor clear" />					<div class="righttext">						<input type="hidden" name="save" value="ok" />						<input type="submit" name="',$txt['tcs_admin_btn_save'],'" value="',$txt['tcs_admin_btn_save'],'" />					</div>				</div>				<span class="botslice"><span></span></span>			</div>		</form>	</div>';}